const config={server:process.env.Database_Server,user:process.env.Database_User,password:process.env.Database_Password,database:process.env.Database,options:{trustedConnection:!0,trustServerCertificate:!0,encrypt:!1,requestTimeout:8e4},pool:{max:10,min:0,idleTimeoutMillis:3e4}};import sql from"mssql";async function checkDuplicate(e,o,t,s=null){let r=`SELECT * FROM ${e} WHERE (${o.map(e=>`${e}=@${e}`).join(" OR ")})`;null!==s&&(o.includes(s)?r+=` AND ${s} != @${s}`:console.warn(`ID '${s}' not found in fields.`));const n=new sql.ConnectionPool(config);try{await n.connect();const e=n.request();return o.forEach((o,s)=>{e.input(o,t[s])}),(await e.query(r)).recordset.length>0}catch(e){throw console.error("Check Duplicate Error:",e),e}}export{checkDuplicate};